import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http'; 
import { User } from '../models/user-model';


const headers = new HttpHeaders({
  'Content-Type': 'application/json'
});

@Component({
  selector: 'app-user-form',
  templateUrl: './user-form.component.html',
  styleUrls: ['./user-form.component.css']
})
export class UserFormComponent {
  user: User = new User();
  apiUrl = 'https://localhost:7133/api/Test/SaveUser'

  constructor(private http: HttpClient) { } 



  //burada post yapılacak.
  saveUser() {

    console.log(this.user);
    this.http.post(this.apiUrl, this.user, { headers: headers }).subscribe(
      (response) => {

        this.showNotification("success", "Başarıyla kaydedildi");
        console.log("Başarıyla kaydedildi...");
      },
      (error) => {

        this.showNotification("error", "Kayıt başarısız");
        console.log("Başarısızz..");
      }
    );

  }


  showNotification(type:string, message:string) {
    const alertElement = document.createElement("div");
    alertElement.classList.add("alert", `alert-${type}`, "mt-3");
    alertElement.textContent = message;

    const alertsContainer = document.getElementById("alerts-container");
    alertsContainer!.appendChild(alertElement);

    // Uyarıyı belirli bir süre sonra kaldırmak için timeout ayarlayabilirsiniz
    setTimeout(() => {
      alertElement.remove();
    }, 3000); // 3 saniye sonra uyarıyı kaldır
  }

}
