import { Component } from '@angular/core';
import { User } from '../models/user-model'; // User entity dosyasının yoluna göre düzenleyin
import { Observable } from 'rxjs';

@Component({
  selector: 'app-user-form',
  templateUrl: './user-form.component.html',
  styleUrls: ['./user-form.component.css']
})
export class UserFormComponent {
  user: User = new User(); // User entity'sini oluşturduğunuz nesne ile değiştirin

  saveUser(user:User):Observable<User> {
    // Kullanıcıyı kaydetmek için burada API isteği gönderebilirsiniz
    return this.http.post<User>(this.apiUrl,user)
    // API isteği nasıl gönderileceğini backend'inize göre ayarlayın
  }
}
<form (ngSubmit)="saveUser()">
  <label for="firstName">First Name:</label>
  <input type="text" id="firstName" [(ngModel)]="user.firstName" name="firstName" required> <br>

  <label for="lastName">Last Name:</label>
  <input type="text" id="lastName" [(ngModel)]="user.lastName" name="lastName" required><br>

  <label for="birthday">Birth Date:</label>
  <input type="string" id="birthday" [(ngModel)]="user.birthday" name="birthday" required><br>

  <label for="city">City:</label>
  <input type="text" id="city" [(ngModel)]="user.city" name="city" required><br>

  <label for="gender">Gender:</label>
  <select id="gender" [(ngModel)]="user.gender" name="gender" required><br>
    <option value="male">Male</option>
    <option value="female">Female</option>
    <option value="other">Other</option>
  </select><br>

  <!-- Diğer input alanlarını da buraya ekleyebilirsiniz -->

  <button type="submit">Save</button>
</form>
