import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http'; // HttpClient'ı import edin
import { User } from '../models/user-model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-user-form',
  templateUrl: './user-form.component.html',
  styleUrls: ['./user-form.component.css']
})
export class UserFormComponent {
  user: User = new User();
  apiUrl = 'https://localhost:7133/api/Test/SaveUser'

  constructor(private http: HttpClient) {} // HttpClient'ı enjekte edin

  //burada post yapılacak.
  saveUser(){
    console.log(this.user);
     this.http.post<User>(this.apiUrl,this.user).subscribe(
      (response) => {
        console.log('User saved:', response);
      },
      (error) => {
        console.error('Error saving user:', error);
        console.log(typeof(this.user));

      }
    );

  }}
